    try {
      await promisePool.query(query, [warehouse_code]);
    } catch (error) {
      throw new Error(
        `Error deleting inventory by warehouse: ${error.message}`
      );
    }
  },
  deleteBasket: async (id) => {
    const query = "DELETE FROM Shopping_Baskets WHERE id = ?";
